mapper.insert:
INSERT INTO pd (pcode, cate, pname, price, pic, wei, kcal)
SELECT
  CONCAT('p', LPAD((SELECT COUNT(*) + 1 FROM pd), 3, '0')),
  #{cate}, #{pname}, #{price}, #{pic}, #{wei}, #{kcal}
FROM dual;

mapper.select:
SELECT pic
FROM pd
WHERE pcode = #{pcode}

SELECT pname
FROM pd
WHERE pcode = #{pcode}

SELECT sum(kcal)
FROM pd
WHERE pcode = #{pcode}

SELECT sum(price)
FROM pd
WHERE pcode = #{pcode}

select pcode, cate, pname, price, pic, wei, kcal 
from pd
where pcode = #{pcode}

mapper.update:
update pd
set pcode=#{pcode}
	,cate=#{cate}
	,pname=#{pname}
	,price = #{price}
	,pic = #{pic}
	,wei=#{wei}
	,kcal=#{kcal}

where pcode=#{pcode}
